CREATE OR REPLACE PROCEDURE san_excluir_hemo_reproduzido ( nr_seq_producao_p text) AS $body$
DECLARE


nr_seq_producao_w	bigint;
ie_pai_reproduzido_w	varchar(1) := 'N';
retorno_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	san_producao
	where	obter_se_contido(nr_seq_prod_origem, nr_seq_producao_p) = 'S'
	and	ie_reproduzido = 'S'
	order by 1;

BEGIN

retorno_w := executar_sql_dinamico('alter trigger san_producao_delete disable', retorno_w);

open C01;
loop
fetch C01 into
	nr_seq_producao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	delete
	from	san_producao
	where	nr_sequencia = nr_seq_producao_w;
	end;
end loop;
close C01;

retorno_w := executar_sql_dinamico('alter trigger san_producao_delete enable', retorno_w);
commit;

end;
$body$
