CREATE OR REPLACE PROCEDURE san_excluir_prod_doacao ( nr_seq_doacao_p bigint) AS $body$
BEGIN
if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then
    delete
    from san_producao_log_alteracao
    where nr_sequencia in (
      SELECT a.nr_sequencia
      from san_producao_log_alteracao a,
           san_producao b
      where a.nr_seq_producao = b.nr_sequencia
      and     b.nr_seq_doacao = nr_seq_doacao_p);


	delete
	from	san_producao
	where	nr_seq_doacao = nr_seq_doacao_p;
end if;

commit;

end;
$body$
