CREATE OR REPLACE PROCEDURE san_gerar_bolsas_campanha ( nr_seq_campanha_p bigint, qt_bolsas_p bigint, nm_usuario_p text) AS $body$
DECLARE


i		integer(10);
qt_bolsas_w	bigint;


BEGIN
qt_bolsas_w := qt_bolsas_p;

if (qt_bolsas_p > 0) then
	for i in 1..qt_bolsas_w loop
		begin
		insert into san_doacao_campanha(nr_Sequencia,
						dt_Atualizacao,
						dt_Atualizacao_nrec,
						nm_usuario,
						nm_usuario_nrec,
						nr_seq_campanha,
						nr_Seq_apresent)
				values (nextval('san_doacao_campanha_seq'),
						clock_timestamp(),
						clock_timestamp(),
						nm_usuario_p,
						nm_usuario_p,
						nr_seq_campanha_p,
						i);
		end;
	end loop;
end if;
commit;

end;
