CREATE OR REPLACE PROCEDURE san_gerar_cirurgias_atend ( ds_seq_materiais_p text, nr_seq_doacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_seq_materiais_w	varchar(4000);
ds_codigo_temp_w	varchar(4000);
nr_seq_doacao_w		bigint;
ds_codigo_aux_w		varchar(4);

k			smallint;
i			smallint;


BEGIN

if (ds_seq_materiais_p IS NOT NULL AND ds_seq_materiais_p::text <> '') then

	k := 1;
	for i in k..length(ds_seq_materiais_p) loop
		begin

		ds_codigo_aux_w	:= substr(ds_seq_materiais_p,i,1);

		if (ds_codigo_aux_w = ',') then

			update	material_atend_paciente
			set	nr_seq_doacao = nr_seq_doacao_p
			where	nr_sequencia =  obter_somente_numero(ds_codigo_temp_w);

			ds_codigo_temp_w := '';
		else
			ds_codigo_temp_w := ds_codigo_temp_w||ds_codigo_aux_w;
		end if;

		end;
	end loop;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
