CREATE OR REPLACE PROCEDURE san_gerar_dados_coleta ( nr_seq_doacao_p bigint, nr_sequencias_p text, ie_braco_puncionado_p text, ie_braco_puncionado_old_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_atualiza_braco_pulsi_w	varchar(1);
ie_gerar_hemocomp_auto_w	varchar(1);
ie_gerar_w		varchar(1);


BEGIN

if (ie_braco_puncionado_p IS NOT NULL AND ie_braco_puncionado_p::text <> '') and (ie_braco_puncionado_p <> ie_braco_puncionado_old_p) then
	begin
	/* Hemoterapia - Parâmetro [156] - Permite atualizar braço puncionado */

	ie_atualiza_braco_pulsi_w := obter_param_usuario(450, 156, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_atualiza_braco_pulsi_w);

	if (ie_atualiza_braco_pulsi_w = 'S') then
		begin
		CALL san_atualizar_braco_pulsionado(
			nr_seq_doacao_p,
			ie_braco_puncionado_p,
			nm_usuario_p);
		end;
	end if;
	end;
end if;

/* Hemoterapia - Parâmetro [159] - Gerar hemocomponentes automaticamente conforme o tipo da bolsa */

ie_gerar_hemocomp_auto_w := obter_param_usuario(450, 159, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_gerar_hemocomp_auto_w);

if (ie_gerar_hemocomp_auto_w = 'C') then
	begin
	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ie_gerar_w
	from	san_producao
	where	nr_seq_doacao = nr_seq_doacao_p;

	if (ie_gerar_w = 'S') then

		CALL san_gerar_derivados_producao(
			nr_seq_doacao_p,
			nr_sequencias_p,
			nm_usuario_p);
	end if;
	end;
end if;

end;
$body$
