SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE san_gerar_exame_adicional ( nr_seq_exame_lote_p bigint, nr_seq_exame_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_exame_w		bigint;
ds_resultado_w		varchar(255);
nr_seq_exame_adic_w	bigint;
qt_reg_w		bigint;
qt_exame_adic_w		bigint;

c01 CURSOR FOR
SELECT	nr_seq_exame_adic
from	san_exame_adicional
where	nr_seq_exame = nr_seq_exame_w
and	ds_resultado = ds_resultado_w;


BEGIN

select	max(nr_seq_exame),
	max(ds_resultado)
into STRICT	nr_seq_exame_w,
	ds_resultado_w
from	san_exame_realizado
where	nr_seq_exame_lote = nr_seq_exame_lote_p
and	nr_seq_exame = nr_seq_exame_p;

-- Inserir exames adicionais
open C01;
loop
fetch C01 into
	nr_seq_exame_adic_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_reg_w
	from	san_exame_realizado
	where	nr_seq_exame_lote = nr_seq_exame_lote_p
	and	nr_seq_exame = nr_seq_exame_adic_w;

	if (qt_reg_w = 0) then

		insert into san_exame_realizado(nr_seq_exame_lote,
			nr_seq_exame,
			dt_atualizacao,
			nm_usuario,
			dt_realizado,
			nr_seq_exame_origem)
		values (nr_seq_exame_lote_p,
			nr_seq_exame_adic_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nr_seq_exame_p);
		commit;
	else if (qt_reg_w > 0) then

		update	san_exame_realizado
		set	nr_seq_exame_origem = nr_seq_exame_p
		where	nr_seq_exame_lote = nr_seq_exame_lote_p
		and	nr_seq_exame = nr_seq_exame_adic_w;

	end if;
	end if;

	end;
end loop;
close C01;

-- Excluir exames adicionais
delete 	from san_exame_realizado a
where 	a.nr_seq_exame_lote = nr_seq_exame_lote_p
and	coalesce(a.dt_liberacao::text, '') = ''
and	(a.ds_resultado IS NOT NULL AND a.ds_resultado::text <> '')
and	exists (SELECT	1
		from	san_exame_adicional b
		where	b.nr_seq_exame = nr_seq_exame_p
		and	b.ie_situacao = 'A'
		and	coalesce(b.cd_estabelecimento,wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento
		and	b.nr_seq_exame_adic = a.nr_seq_exame
		and	b.ds_resultado <> ds_resultado_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
