CREATE OR REPLACE PROCEDURE san_gerar_paciente_fim_prod (nr_seq_doacao_p bigint, nm_usuario_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_pesosa_fisica_w	varchar(10);
nr_atendimento_w	bigint;
ie_existe_doacao_w	varchar(1);

BEGIN
if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then
	select	max(a.nr_atendimento),
		max(a.cd_pessoa_fisica)
	into STRICT	nr_atendimento_w,
		cd_pesosa_fisica_w
	from	san_doacao a
	where	a.nr_sequencia = nr_seq_doacao_p;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_existe_doacao_w
	from	san_fim_prod_dia a
	where	a.nr_seq_doacao = nr_seq_doacao_p
	and	not exists (SELECT 1
			   from    san_doacao b
			   where   b.nr_sequencia = a.nr_seq_doacao
			   and	   ie_status in (4,5,8,6));

	if (ie_existe_doacao_w = 'N') then
		insert into san_fim_prod_dia(
			nr_sequencia,
			nr_seq_doacao,
			cd_pessoa_fisica,
			nr_atendimento,
			nm_usuario,
			nm_usuario_nrec,
			dt_atualizacao,
			dt_atualizacao_nrec,
			cd_estabelecimento
		) values (
			nextval('san_fim_prod_dia_seq'),
			nr_seq_doacao_p,
			cd_pesosa_fisica_w,
			nr_atendimento_w,
			nm_usuario_p,
			nm_usuario_p,
			clock_timestamp(),
			clock_timestamp(),
			cd_estabelecimento_p);
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
