SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE san_gerar_questionario ( nr_seq_doacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	nr_sequencia
	from	san_impedimento
	order by nr_sequencia;

qt_registros_w		bigint;
nr_seq_impedimento_w	bigint;
ie_auto_resposta_w	varchar(1);
ie_resposta_w		varchar(1);


BEGIN

ie_auto_resposta_w := obter_param_usuario(450, 194, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_auto_resposta_w);

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

	open C01;
	loop
	fetch C01 into
		nr_seq_impedimento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select 	CASE WHEN ie_auto_resposta_w='S' THEN coalesce(ie_resposta_correta,'N')  ELSE null END  ie_resposta
		into STRICT	ie_resposta_w
		from 	san_impedimento
		where	nr_sequencia = nr_seq_impedimento_w;

		select 	count(*)
		into STRICT	qt_registros_w
		from	san_questionario
		where	nr_seq_doacao 	 	= nr_seq_doacao_p
		and	nr_seq_impedimento 	= nr_seq_impedimento_w;

		if (qt_registros_w = 0) then
			insert into san_questionario(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_doacao,
				nr_seq_impedimento,
				ie_resposta)
			values (	nextval('san_questionario_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_doacao_p,
				nr_seq_impedimento_w,
				ie_resposta_w
			);
		end if;

		end;
	end loop;
	close C01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
