CREATE OR REPLACE PROCEDURE san_gerar_solicitacao ( nr_seq_entidade_p bigint, nm_usuario_p text, cd_estabelecimento_P bigint, nr_seq_solicitacao_p INOUT text) AS $body$
DECLARE

nr_seq_solicitacao_w	bigint;
cd_pf_usuario_w		varchar(100);

BEGIN
if (nr_seq_entidade_p IS NOT NULL AND nr_seq_entidade_p::text <> '') then 
	 
	select	nextval('san_solicitacao_seq') 
	into STRICT	nr_seq_solicitacao_w 
	;
	 
	select	substr(Obter_Pf_Usuario(nm_usuario_p,'C'),1,100) 
	into STRICT	cd_pf_usuario_w 
	;
	 
	 
	insert into san_solicitacao(	 
		nr_sequencia, 
		nr_seq_entidade, 
		cd_pf_solic, 
		dt_solicitacao, 
		cd_estabelecimento, 
		dt_atualizacao, 
		nm_usuario, 
		ie_tipo_solicitacao, 
		nm_usuario_nrec, 
		dt_atualizacao_nrec) 
	values (	nr_seq_solicitacao_w, 
		nr_seq_entidade_p, 
		cd_pf_usuario_w, 
		clock_timestamp(), 
		cd_estabelecimento_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		'E', 
		nm_usuario_p, 
		clock_timestamp());
	 
	nr_seq_solicitacao_p := nr_seq_solicitacao_w;
	 
end if;	
 
commit;
 
end;
$body$
