CREATE OR REPLACE PROCEDURE san_gerar_solic_item_reserv ( nr_seq_solicitacao_p bigint, nr_seq_reserva_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_derivado_w	bigint;
qt_solicitada_w		san_reserva_item.qt_solicitada%type;
ie_irradiado_w		varchar(1);
ie_lavado_w		varchar(1);
ie_filtrado_w		varchar(1);
ie_aliquotado_w		varchar(1);
ie_tipo_sangue_w	varchar(2);
ie_fator_rh_w		varchar(1);
ie_aferese_w		san_reserva_item.ie_aferese%type;
ie_pool_w		san_reserva_item.ie_pool%type;

C01 CURSOR FOR
	SELECT  nr_seq_derivado,
		qt_solicitada,
		coalesce(ie_irradiado,'N'),
		coalesce(ie_lavado,'N'),
		coalesce(ie_filtrado,'N'),
		coalesce(ie_aliquotado,'N'),
		coalesce(ie_aferese,'N'),
		coalesce(ie_pool,'N')
	from 	san_reserva_item
	where 	nr_seq_reserva = nr_seq_reserva_p;


BEGIN

if (nr_seq_solicitacao_p IS NOT NULL AND nr_seq_solicitacao_p::text <> '') then

	select	max(b.ie_tipo_sangue),
		max(b.ie_fator_rh)
	into STRICT	ie_tipo_sangue_w,
		ie_fator_rh_w
	from	san_reserva a,
		pessoa_fisica b
	where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and	a.nr_sequencia = nr_seq_reserva_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_derivado_w,
		qt_solicitada_w,
		ie_irradiado_w,
		ie_lavado_w,
		ie_filtrado_w,
		ie_aliquotado_w,
		ie_aferese_w,
		ie_pool_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		insert into san_solic_item(
			nr_sequencia,
			nr_seq_solic,
			nr_seq_derivado,
			qt_solicitada,
			ie_irradiado,
			ie_lavado,
			ie_filtrado,
			ie_aliquotado,
			dt_atualizacao,
			nm_usuario,
			ie_tipo_sangue,
			ie_fator_rh,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			nr_seq_reserva,
			ie_aferese,
			ie_pool)
		values (	nextval('san_solic_item_seq'),
			nr_seq_solicitacao_p,
			nr_seq_derivado_w,
			qt_solicitada_w,
			ie_irradiado_w,
			ie_lavado_w,
			ie_filtrado_w,
			ie_aliquotado_w,
			clock_timestamp(),
			nm_usuario_p,
			ie_tipo_sangue_w,
			ie_fator_rh_w,
			nm_usuario_p,
			clock_timestamp(),
			nr_seq_reserva_p,
			ie_aferese_w,
			ie_pool_w);

		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
