CREATE OR REPLACE PROCEDURE san_grava_log_producao_alterac (nr_seq_producao_p bigint, qt_volume_anterior_p bigint, qt_volume_atual_p bigint, ie_update_p text, nm_usuario_p text ) AS $body$
BEGIN
if	((nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') and (qt_volume_anterior_p IS NOT NULL AND qt_volume_anterior_p::text <> '') and (qt_volume_atual_p IS NOT NULL AND qt_volume_atual_p::text <> '')) then

	insert into san_producao_log_alteracao(
		nr_sequencia,
		nr_seq_producao,
		dt_alteracao,
		qt_volume_ant,
		qt_volume_atual,
		nm_usuario,
		nm_usuario_nrec,
		dt_atualizacao,
		dt_atualizacao_nrec
	) values (
		nextval('san_producao_log_alteracao_seq'),
		nr_seq_producao_p,
		clock_timestamp(),
		qt_volume_anterior_p,
		qt_volume_atual_p,
		nm_usuario_p,
		nm_usuario_p,
		clock_timestamp(),
		clock_timestamp()
	);

	if (ie_update_p = 'S') then
		update	san_producao
		set	qt_volume	= qt_volume_atual_p
		where	nr_sequencia	= nr_seq_producao_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
