CREATE OR REPLACE PROCEDURE san_insere_lote_bolsa (nr_seq_doacao_p bigint, nr_seq_lote_fornec_p bigint, nr_seq_sangria_p bigint, ds_lote_p text, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE


/*
D - Coleta (opção de mouse 'Confirmar coleta')
C - Coleta (opção de mouse 'Baixar material estoque')
S - Sangria
*/
nr_seq_lote_w		bigint;


BEGIN
if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') and (nr_seq_lote_fornec_p IS NOT NULL AND nr_seq_lote_fornec_p::text <> '') and (nr_seq_lote_fornec_p > 0) and (ie_opcao_p = 'C') then

	insert into san_lote_bolsa(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_doacao,
		nr_seq_lote_fornec)
	values (	nextval('san_lote_bolsa_seq'),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_doacao_p,
		nr_seq_lote_fornec_p);

elsif (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') and (ds_lote_p IS NOT NULL AND ds_lote_p::text <> '') and (ie_opcao_p = 'D') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_lote_w
	from	material_lote_fornec
	where	ds_lote_fornec = ds_lote_p;

	if (nr_seq_lote_w IS NOT NULL AND nr_seq_lote_w::text <> '') then
		insert into san_lote_bolsa(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			nr_seq_doacao,
			nr_seq_lote_fornec)
		values (	nextval('san_lote_bolsa_seq'),
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p,
			clock_timestamp(),
			nr_seq_doacao_p,
			nr_seq_lote_w);
	end if;

elsif (nr_seq_sangria_p IS NOT NULL AND nr_seq_sangria_p::text <> '') and (nr_seq_lote_fornec_p IS NOT NULL AND nr_seq_lote_fornec_p::text <> '') and (nr_seq_lote_fornec_p > 0) and (ie_opcao_p = 'S') then

	insert into san_lote_bolsa(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_sangria,
		nr_seq_lote_fornec)
	values (	nextval('san_lote_bolsa_seq'),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_sangria_p,
		nr_seq_lote_fornec_p);
end if;

commit;

end;
