CREATE OR REPLACE PROCEDURE san_inserir_crioprotetores ( nr_seq_producao_p bigint, nr_seq_lote_p bigint, cd_material_p text, qt_volume_p bigint, ds_observacao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
