cd_operacao_estoque_w			san_parametro.cd_operacao_estoque%type;


BEGIN
select	max(a.cd_operacao_estoque)
into STRICT	cd_operacao_estoque_w
from	san_parametro	a
where	a.cd_estabelecimento	= cd_estabelecimento_p;

insert into san_dados_congelacao(	nr_sequencia,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_atualizacao,
