values (	nextval('san_dados_congelacao_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_producao_p,
		cd_operacao_estoque_w,
		ds_observacao_p,
		cd_material_p,
		qt_volume_p,
		CASE WHEN nr_seq_lote_p=0 THEN null  ELSE nr_seq_lote_p END ,
		1);

update	san_producao
set	qt_volume	= qt_volume + qt_volume_p
where	nr_sequencia	= nr_seq_producao_p;

