SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE san_inserir_hemo_reproc_lista ( nr_seq_doacao_p bigint, nr_seq_producao_p bigint, nr_seq_derivado_lista_p text, qt_volume_lista_p text, qt_peso_lista_p text, nm_usuario_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint, lista_der_nao_chec_p text) AS $body$
DECLARE

 
nr_seq_derivado_lista_w	varchar(2000);
nr_seq_derivado_w	bigint;
qt_volume_lista_w	varchar(2000);
qt_peso_lista_w		varchar(2000);
qt_volume_w		bigint;
qt_peso_bolsa_w		double precision;
ie_rep_obrigatoria_w	varchar(1);
lista_der_nao_chec_w	varchar(2000);
nr_seq_der_nao_chec_w	varchar(10);


BEGIN 
 
nr_seq_derivado_lista_w	:= nr_seq_derivado_lista_p;
qt_volume_lista_w	:= qt_volume_lista_p;
qt_peso_lista_w		:= qt_peso_lista_p;
lista_der_nao_chec_w	:= lista_der_nao_chec_p;
 
if (nr_seq_derivado_lista_w IS NOT NULL AND nr_seq_derivado_lista_w::text <> '') then 
	nr_seq_derivado_w	:= substr(nr_seq_derivado_lista_w, 1, position(',' in nr_seq_derivado_lista_w) - 1);
	 
	if (nr_seq_derivado_w IS NOT NULL AND nr_seq_derivado_w::text <> '') then 
		while (lista_der_nao_chec_w IS NOT NULL AND lista_der_nao_chec_w::text <> '') loop 
			begin 
			 
			nr_seq_der_nao_chec_w	:= substr(lista_der_nao_chec_w, 1, position(',' in lista_der_nao_chec_w) - 1);
			lista_der_nao_chec_w	:= substr(lista_der_nao_chec_w, position(',' in lista_der_nao_chec_w) + 1, length(lista_der_nao_chec_w));
			 
			select 	coalesce(max(ie_obriga_reproducao),'N') 
			into STRICT	ie_rep_obrigatoria_w 
			from	san_derivado_reproducao 
			where	nr_seq_derivado_rep = nr_seq_der_nao_chec_w;
			 
			if (ie_rep_obrigatoria_w = 'S') then 
				/*Existem hemocomponentes obrigatórios para ser realizada reprodução não checados.*/
 
				CALL wheb_mensagem_pck.exibir_mensagem_abort(183080);
			end if;		
			end;
		end loop;
	end if;
end if;	
 
while (nr_seq_derivado_lista_w IS NOT NULL AND nr_seq_derivado_lista_w::text <> '') loop 
	begin	 
	 
	nr_seq_derivado_w	:= substr(nr_seq_derivado_lista_w, 1, position(',' in nr_seq_derivado_lista_w) - 1);
	nr_seq_derivado_lista_w	:= substr(nr_seq_derivado_lista_w, position(',' in nr_seq_derivado_lista_w) + 1, length(nr_seq_derivado_lista_w));
 
	qt_volume_w		:= substr(qt_volume_lista_w, 1, position('@' in qt_volume_lista_w) - 1);
	qt_volume_lista_w		:= substr(qt_volume_lista_w, position('@' in qt_volume_lista_w) + 1, length(qt_volume_lista_w));
	 
	qt_peso_bolsa_w		:= substr(qt_peso_lista_w, 1, position('@' in qt_peso_lista_w) - 1);
	qt_peso_lista_w		:= substr(qt_peso_lista_w, position('@' in qt_peso_lista_w) + 1, length(qt_peso_lista_w));
 
	if (nr_seq_derivado_w IS NOT NULL AND nr_seq_derivado_w::text <> '') then 
		begin 
		CALL san_inserir_hemo_reproc( 
			nr_seq_doacao_p, 
			nr_seq_producao_p, 
			nr_seq_derivado_w, 
			qt_volume_w, 
			qt_peso_bolsa_w, 
			nm_usuario_p, 
			cd_perfil_p, 
			cd_estabelecimento_p);
		end;
	end if;
	end;
end loop;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
