CREATE OR REPLACE PROCEDURE san_inserir_motivo_baixa_mat ( nr_seq_sangria_p bigint, nr_seq_doacao_p bigint, ds_motivo_p text) AS $body$
DECLARE


nr_seq_lote_bolsa_w	bigint;


BEGIN
if (nr_seq_sangria_p IS NOT NULL AND nr_seq_sangria_p::text <> '') and (ds_motivo_p IS NOT NULL AND ds_motivo_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_lote_bolsa_w
	from	san_lote_bolsa
	where	nr_seq_sangria = nr_seq_sangria_p;

	if (nr_seq_lote_bolsa_w IS NOT NULL AND nr_seq_lote_bolsa_w::text <> '')  then

		update	san_lote_bolsa
		set	ds_motivo  =	ds_motivo_p
		where	nr_sequencia = nr_seq_lote_bolsa_w;

	end if;
elsif (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_lote_bolsa_w
	from	san_lote_bolsa
	where	nr_seq_doacao = nr_seq_doacao_p;

	if (nr_seq_lote_bolsa_w IS NOT NULL AND nr_seq_lote_bolsa_w::text <> '')  then

		update	san_lote_bolsa
		set	ds_motivo  =	ds_motivo_p
		where	nr_sequencia = nr_seq_lote_bolsa_w;

	end if;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
