CREATE OR REPLACE PROCEDURE san_inserir_quest_perguntas ( nm_usuario_p text, nr_seq_doacao_p bigint, nr_seq_pergunta_p bigint, ie_resposta_p text, ds_observacao_p text, dt_ocorrencia timestamp) AS $body$
DECLARE

nr_sequencia_w		bigint;
dt_ocorrencia_w		timestamp;


BEGIN

dt_ocorrencia_w := dt_ocorrencia;
if (nr_seq_doacao_p <> 0) then
	begin
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from 	san_doacao_questionario
	where	nr_seq_doacao = nr_seq_doacao_p
	and	nr_seq_pergunta	= nr_seq_pergunta_p;

	if (nr_sequencia_w = 0) then

		insert into san_doacao_questionario( nr_sequencia,
						nm_usuario_nrec,
						dt_atualizacao_nrec,
						nm_usuario,
						dt_atualizacao,
						nr_seq_doacao,
						nr_seq_pergunta,
						ds_observacao,
						ie_resposta,
						dt_ocorrencia)
					values (nextval('san_doacao_questionario_seq'),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nr_seq_doacao_p,
						nr_seq_pergunta_p,
						substr(ds_observacao_p,1,255),
						ie_resposta_p,
						dt_ocorrencia_w);
	else
		update	san_doacao_questionario
		set	nm_usuario     = nm_usuario_p,
			dt_atualizacao = clock_timestamp(),
			ds_observacao  = substr(ds_observacao_p,1,255),
			ie_resposta    = ie_resposta_p,
			dt_ocorrencia  = dt_ocorrencia_w
		where	nr_sequencia = nr_sequencia_w
		and	nr_seq_doacao = nr_seq_doacao_p
		and	nr_seq_pergunta = nr_seq_pergunta_p;
	end if;
	end;
end if;
commit;

end;
$body$
