CREATE OR REPLACE PROCEDURE san_inutiliza_sangria ( nr_seq_sangria_p bigint, nr_seq_motivo_p bigint, ds_observacao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

cd_pessoa_fisica_w	varchar(10);
nr_seq_inutil_w		bigint;

BEGIN
select 	cd_pessoa_fisica
into STRICT	cd_pessoa_fisica_w
from	usuario
where	nm_usuario = nm_usuario_p;

select	nextval('san_inutilizacao_seq')
into STRICT	nr_seq_inutil_w
;

if (nr_seq_sangria_p IS NOT NULL AND nr_seq_sangria_p::text <> '') then

	insert into san_inutilizacao(
		nr_sequencia,
		dt_inutilizacao,
		nr_seq_motivo_inutil,
		dt_atualizacao,
		nm_usuario,
		cd_pf_realizou,
		ds_observacao,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_fechamento,
		cd_estabelecimento,
		nm_usuario_fechamento)
	values ( nr_seq_inutil_w,
		clock_timestamp(),
		nr_seq_motivo_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_pessoa_fisica_w,
		ds_observacao_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		cd_estabelecimento_p,
		nm_usuario_p);

	update	san_sangria_terapeutica
	set	nr_seq_inutil 	= nr_seq_inutil_w,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp(),
		dt_inutilizacao = clock_timestamp(),
		nm_usuario_inut = nm_usuario_p
	where	nr_sequencia	= nr_seq_sangria_p;
end if;

commit;

end;
$body$
