CREATE OR REPLACE PROCEDURE san_irradiar_hemocomponente ( nr_seq_producao_p bigint, dt_vencimento_p timestamp, nm_usuario_p text, ds_justificativa_p text) AS $body$
BEGIN

if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') then
	begin
	update	san_producao
	set	ie_irradiado 			= 	'S',
		nm_usuario_irradiacao	=	nm_usuario_p,
		dt_vencimento			=	dt_vencimento_p,
		ds_just_irradiacao  	= ds_justificativa_p,
		dt_irradiacao = clock_timestamp()
	where	nr_sequencia		=	nr_seq_producao_p;
	end;
end if;

commit;

end;
$body$
