CREATE OR REPLACE PROCEDURE san_log_cancelamento_transf ( nr_seq_producao_p bigint, nr_seq_transf_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_producao_p > 0) and (nr_seq_transf_p > 0) then
	insert	into san_transf_log_canc(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				nr_seq_producao,
				nr_seq_transfusao,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
			values (
				nextval('san_transf_log_canc_seq'),
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_producao_p,
				nr_seq_transf_p,
				clock_timestamp(),
				nm_usuario_p);

	update 	san_producao
	set	nr_seq_transfusao  = NULL
	where	nr_sequencia = nr_seq_producao_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
