CREATE OR REPLACE PROCEDURE san_ocorrencia_doador_apto ( nr_seq_doacao_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_san_motivo_ocorrencia_w		san_motivo_ocorrencia.nr_sequencia%type;
nr_seq_tipo_ocorrencia_w		san_motivo_ocorrencia.nr_seq_tipo_ocoreencia%type;

BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_san_motivo_ocorrencia_w
from	san_motivo_ocorrencia
where	ie_gera_alt_apto = 'S'
and	coalesce(ie_situacao, 'A') = 'A';

if (nr_seq_san_motivo_ocorrencia_w IS NOT NULL AND nr_seq_san_motivo_ocorrencia_w::text <> '') then

	select	max(nr_seq_tipo_ocoreencia)
	into STRICT	nr_seq_tipo_ocorrencia_w
	from	san_motivo_ocorrencia
	where	nr_sequencia = nr_seq_san_motivo_ocorrencia_w;

	insert into san_ocorrencia_doador(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_observacao,
		nr_seq_motivo_ocorrencia,
		nr_seq_doacao,
		nr_seq_tipo_ocoreencia,
		dt_liberacao,
		nm_usuario_lib
	) values (
		nextval('san_ocorrencia_doador_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_observacao_p,
		nr_seq_san_motivo_ocorrencia_w,
		nr_seq_doacao_p,
		nr_seq_tipo_ocorrencia_w,
		clock_timestamp(),
		nm_usuario_p
	);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
