SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE san_receber_derivado ( nr_seq_envio_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_envio_retorno_w	bigint;
nr_seq_producao_w	bigint;
dt_validade_w	timestamp;

BEGIN

if (nr_seq_envio_p IS NOT NULL AND nr_seq_envio_p::text <> '') then

	select 	max(nr_seq_envio),
		max(nr_seq_producao),
		max(dt_validade)
	into STRICT	nr_seq_envio_retorno_w,
		nr_seq_producao_w,
		dt_validade_w
	from	san_envio_derivado_val
	where	nr_sequencia = nr_seq_envio_p;


	update 	san_envio_derivado_val
	set	dt_recebimento 		= clock_timestamp(),
		nm_usuario_recebimento	= nm_usuario_p,
		ie_status		= 'R'
	where	nr_sequencia		= nr_seq_envio_p;

	update	san_producao a
	set	a.ie_irradiado = 'S',
		a.dt_vencimento = coalesce(dt_validade_w, a.dt_vencimento),
		a.nm_usuario = nm_usuario_p,
		a.dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_producao_w;

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
