SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE san_register_bag_campaign ( nr_seq_campanha_p bigint, nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE



ds_lote_fornec_w 	varchar(20);
nr_seq_apresent_w	bigint := 1;


BEGIN
	if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then
		begin

		select 	ds_lote_fornec
		into STRICT	ds_lote_fornec_w
		from 	material_lote_fornec
		where nr_sequencia = nr_seq_lote_p;

		SELECT  coalesce(MAX(nr_seq_apresent),0) + 1
		into STRICT	nr_seq_apresent_w
		FROM    san_doacao_campanha
		WHERE 	nr_seq_campanha = nr_seq_campanha_p;

		insert into san_doacao_campanha(
				nr_sequencia        ,
				dt_atualizacao      ,
				nm_usuario          ,
				dt_atualizacao_nrec ,
				nm_usuario_nrec     ,
				nr_seq_campanha     ,
				nr_bolsa            ,
				nr_seq_lote,
				nr_seq_apresent)
			values (	nextval('san_doacao_campanha_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp()	,
				nm_usuario_p,
				nr_seq_campanha_p,
				ds_lote_fornec_w,
				nr_seq_lote_p,
				nr_seq_apresent_w	);
		end;
	end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
