CREATE OR REPLACE PROCEDURE san_registrar_bolsa_isbt ( cd_bolsa_p text, nr_bolsa_isbt_p text, nr_prescricao_p bigint, nr_seq_procedimento_p bigint, nr_bolsa_p text, qt_volume_p bigint, nr_seq_reserva_p bigint, nr_seq_item_p bigint, nm_usuario_p text, ds_mensagem_p INOUT text, ie_lavado_p text, ie_irradiado_p text, ie_filtrado_p text, ie_aliquotado_p text) AS $body$
DECLARE


ie_proc_bolsa_w		varchar(1);
nm_paciente_w  		varchar(255);
nr_seq_horario_w      	bigint		:= 0;


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_proc_bolsa_w
from	prescr_proc_bolsa
where	cd_bolsa		= cd_bolsa_p
and	nr_bolsa_isbt	= nr_bolsa_isbt_p
and	coalesce(dt_cancelamento::text, '') = '';

if (ie_proc_bolsa_w = 'S') then
	begin
	select	max(a.nr_atendimento||' - '||max(substr(obter_nome_pf(a.cd_pessoa_fisica),1,255)))
	into STRICT	nm_paciente_w
	from	san_reserva a,
		prescr_proc_bolsa b
	where	a.nr_prescricao = b.nr_prescricao
	and	b.cd_bolsa = cd_bolsa_p
	and	b.nr_bolsa_isbt = nr_bolsa_isbt_p
	and	coalesce(b.dt_cancelamento::text, '') = ''
	group by  a.nr_atendimento;

	ds_mensagem_p	:= obter_texto_dic_objeto(90706, wheb_usuario_pck.get_nr_seq_idioma, 'NM_PACIENTE=' ||nm_paciente_w);
	end;
else
	begin
	nr_seq_horario_w := gerar_registro_isbt(
		nr_prescricao_p, nr_seq_procedimento_p, nr_bolsa_p, nr_bolsa_isbt_p, cd_bolsa_p, qt_volume_p, nm_usuario_p, ie_lavado_p, ie_irradiado_p, ie_filtrado_p, ie_aliquotado_p, nr_seq_horario_w, nr_seq_horario_w);

	CALL san_atualizar_qt_atendida(
		nr_seq_reserva_p,
		nr_seq_item_p,
		qt_volume_p,
		nm_usuario_p);
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
