CREATE OR REPLACE PROCEDURE san_registra_recebimento_bolsa (nr_seq_doacao_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then
	update	san_doacao
	set	dt_recebimento_bolsa	= clock_timestamp(),
		dt_atualizacao		= clock_timestamp(),
		nm_usuario_recebimento	= nm_usuario_p,
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_doacao_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
