CREATE OR REPLACE PROCEDURE san_regitrar_entrega_brinde ( nr_seq_doacao_p bigint, nr_seq_brinde_p bigint, dt_entrega_p timestamp, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE

cd_doador_w		varchar(20);
cd_pessoa_entrega_w	varchar(20);


BEGIN
if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then
	select	max(x.cd_pessoa_fisica)
	into STRICT	cd_doador_w
	from	san_doacao x
	where	x.nr_sequencia = nr_seq_doacao_p;

	select	max(x.cd_pessoa_fisica)
	into STRICT	cd_pessoa_entrega_w
	from	usuario x
	where	x.nm_usuario = nm_usuario_p;


	insert into san_entrega_brinde(
			nr_sequencia,
			cd_estabelecimento,
	                cd_pessoa_entrega,
	                cd_pessoa_fisica,
	                dt_atualizacao,
	                dt_entrega,
	                nm_usuario,
	                nr_seq_brinde,
	                nr_seq_doacao,
	                ds_observacao
	) values (
			nextval('san_entrega_brinde_seq'),
			wheb_usuario_pck.get_cd_estabelecimento,
			cd_pessoa_entrega_w,
			cd_doador_w,
			clock_timestamp(),
			dt_entrega_p,
			nm_usuario_p,
			nr_seq_brinde_p,
			nr_seq_doacao_p,
			ds_observacao_p
	);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
