CREATE OR REPLACE PROCEDURE san_reserva_item_nr_js ( nr_seq_item_res_p bigint, nm_usuario_p text, nr_seq_p INOUT bigint, ds_lavado_p INOUT text, ds_aliquotado_p INOUT text, ds_filtrado_p INOUT text, ds_irradiado_p INOUT text) AS $body$
BEGIN

select 	max(nr_seq_item) + 1
into STRICT	nr_seq_p
from 	san_reserva_item
where 	nr_seq_reserva = nr_seq_item_res_p;

select	substr(obter_dados_prescricao_hemo(max(a.nr_prescricao),max(a.nr_seq_prescr),'L'),1,1) ds_lavado,
	substr(obter_dados_prescricao_hemo(max(a.nr_prescricao),max(a.nr_seq_prescr),'A'),1,1) ds_aliquotado,
	substr(obter_dados_prescricao_hemo(max(a.nr_prescricao),max(a.nr_seq_prescr),'F'),1,1) ds_filtrado,
	substr(obter_dados_prescricao_hemo(max(a.nr_prescricao),max(a.nr_seq_prescr),'I'),1,1) ds_irradiado
into STRICT	ds_lavado_p,
	ds_aliquotado_p,
	ds_filtrado_p,
	ds_irradiado_p
from  	san_reserva_item a
where	a.nr_seq_reserva = nr_seq_item_res_p
and	a.nr_seq_item = nr_seq_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
