SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE san_reutilizar_hemocomponente ( nr_seq_doacao_p bigint, nr_seq_producao_p bigint, qt_volume_bolsa_p text, nm_usuario_p text) AS $body$
DECLARE


qt_volume_atual_w	smallint;
qt_volume_w		smallint;


BEGIN

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') and (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') then

	select  (a.qt_volume_atual + qt_volume_bolsa_p)::numeric
	into STRICT	qt_volume_atual_w
	from 	san_doacao a
	where 	a.nr_sequencia = nr_seq_doacao_p;

	select  (b.qt_volume - qt_volume_bolsa_p)::numeric
	into STRICT	qt_volume_w
	from 	san_producao b
	where 	b.nr_sequencia = nr_seq_producao_p;


	update 		san_doacao
	set 		qt_volume_atual = qt_volume_atual_w,
			nm_usuario 	= nm_usuario_p
	where 		nr_sequencia 	= nr_seq_doacao_p;

	update 		san_producao
	set 		qt_volume 	= qt_volume_w,
			nm_usuario 	= nm_usuario_p
	where 		nr_sequencia 	= nr_seq_producao_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
