CREATE OR REPLACE PROCEDURE san_update_apt ( NR_SEQ_DOACAO_P SAN_DOACAO.NR_SEQUENCIA%TYPE, IE_APT_P SAN_DOACAO.IE_AVALIACAO_FINAL%TYPE, NM_USUARIO_P text ) AS $body$
BEGIN

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

	update san_doacao
	set	ie_avaliacao_final	= ie_apt_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where 	nr_sequencia = nr_seq_doacao_p
	and	ie_avaliacao_final	<> 'I';

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
