CREATE OR REPLACE PROCEDURE san_update_question ( nr_sequencia_p bigint, nm_usuario_p text, nr_seq_doacao_p bigint, nr_seq_impedimento_p bigint, ds_observacao_p text, dt_ocorrencia_p timestamp, ie_resposta_p text, ie_impede_doacao_p text, nr_seq_new_question_p INOUT bigint ) AS $body$
DECLARE


nr_sequencia_w san_questionario.nr_sequencia%type;


BEGIN

nr_sequencia_w := nr_sequencia_p;
if (coalesce(nr_sequencia_p::text, '') = '') then

	select 	nextval('san_questionario_seq')
	into STRICT 	nr_sequencia_w
	;

	insert into san_questionario(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_doacao,
		nr_seq_impedimento,
		ds_observacao,
		dt_ocorrencia,
		ie_resposta,
		ie_impede_doacao)
 	values (
  		nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_doacao_p,
		nr_seq_impedimento_p,
		ds_observacao_p,
		dt_ocorrencia_p,
		ie_resposta_p,
		ie_impede_doacao_p);
else
	update	san_questionario
	set		dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			ds_observacao = ds_observacao_p,
			dt_ocorrencia = dt_ocorrencia_p,
			ie_resposta = ie_resposta_p,
			ie_impede_doacao = ie_impede_doacao_p
	where nr_sequencia = nr_sequencia_p;
end if;

nr_seq_new_question_p := nr_sequencia_w;
commit;

end;
