CREATE OR REPLACE PROCEDURE san_validar_auto_exclusao ( cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, nr_seq_doacao_p INOUT bigint, ds_mensagem_auto_exc_p INOUT text, ds_mensagem_ok_p INOUT text, ds_mensagem_sem_auto_exc_p INOUT text) AS $body$
BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_doacao_p
from	san_doacao
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	coalesce(ie_auto_exclusao::text, '') = '';

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then
	begin
	select	ds_mensagem_auto_exc
	into STRICT	ds_mensagem_auto_exc_p
	from	san_parametro
	where	cd_estabelecimento = cd_estabelecimento_p  LIMIT 1;

	ds_mensagem_ok_p	:= obter_texto_tasy(90658, wheb_usuario_pck.get_nr_seq_idioma);
	end;
else
	ds_mensagem_sem_auto_exc_p	:= obter_texto_tasy(93031, wheb_usuario_pck.get_nr_seq_idioma);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
