CREATE OR REPLACE PROCEDURE san_verifica_inutil_hemo_rep (nr_seq_san_producao_p bigint, nr_seq_derivado_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
nr_seq_inutil_w		bigint;
nr_seq_motivo_w		bigint;

C01 CURSOR FOR
	SELECT	nr_seq_motivo_inutil
	from	san_derivado_reprod_inutil
	where	nr_seq_derivado = nr_seq_derivado_p
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_motivo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (nr_seq_motivo_w IS NOT NULL AND nr_seq_motivo_w::text <> '') then
		select	nextval('san_inutilizacao_seq')
		into STRICT	nr_seq_inutil_w
		;

		select 	cd_pessoa_fisica
		into STRICT	cd_pessoa_fisica_w
		from	usuario
		where	nm_usuario = nm_usuario_p;

		insert into san_inutilizacao(
			nr_sequencia,
			dt_inutilizacao,
			nr_seq_motivo_inutil,
			dt_atualizacao,
			nm_usuario,
			cd_pf_realizou,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_estabelecimento)
		values ( nr_seq_inutil_w,
			clock_timestamp(),
			nr_seq_motivo_w,
			clock_timestamp(),
			nm_usuario_p,
			cd_pessoa_fisica_w,
			clock_timestamp(),
			nm_usuario_p,
			cd_estabelecimento_p);

		update	san_producao
		set	nr_seq_inutil 		= nr_seq_inutil_w,
			ie_local_inutilizacao	= 'P',
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_seq_derivado		= nr_seq_derivado_p
		and	nr_sequencia		= nr_seq_san_producao_p;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
