CREATE OR REPLACE PROCEDURE san_vincular_campanha (nr_seq_doacao_p bigint, nm_usuario_p text, nr_seq_campanha_p bigint, ie_opcao_p text) AS $body$
DECLARE


nr_seq_apresent_w 	smallint;


BEGIN

if (ie_opcao_p  = 'V') then
	if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') and (nr_seq_campanha_p IS NOT NULL AND nr_seq_campanha_p::text <> '') then

		select 	coalesce(max(nr_seq_apresent),0) + 1
		into STRICT	nr_seq_apresent_w
		from	san_doacao_campanha
		where   nr_seq_campanha = nr_seq_campanha_p;

		insert into san_doacao_campanha(nr_sequencia,
				nr_seq_campanha,
				nm_usuario_nrec,
				dt_atualizacao_nrec,
				nm_usuario,
				dt_atualizacao,
				nr_seq_doacao,
				nr_seq_apresent)
			values (nextval('san_doacao_campanha_seq'),
				nr_seq_campanha_p,
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nr_seq_doacao_p,
				nr_seq_apresent_w);

		update 	san_doacao
		set	nr_seq_campanha = nr_seq_campanha_p
		where 	nr_sequencia = nr_seq_doacao_p;

	end if;
end if;

if (ie_opcao_p = 'D') then
	if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') and (nr_seq_campanha_p IS NOT NULL AND nr_seq_campanha_p::text <> '') then

		delete 	from san_doacao_campanha
		where 	nr_seq_doacao = nr_seq_doacao_p
		and	nr_seq_campanha = nr_seq_campanha_p;

		update san_doacao
		set    nr_seq_campanha  = NULL
		where  nr_sequencia = nr_seq_doacao_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
