CREATE OR REPLACE PROCEDURE san_vincular_cirurgia (nr_atendimento_p bigint, nr_sequencia_p bigint, nr_cirurgia_p bigint, nr_atendimento_cirurgia_p bigint, ie_pasta_p text ) AS $body$
DECLARE


