		where	nr_seq_reserva 	= nr_sequencia_p
		and (nr_cirurgia 	<> nr_cirurgia_p
		or	coalesce(nr_cirurgia::text, '') = '');

	end if;

	if (ie_pasta_p ='T') then

		update	san_transfusao
		set	nr_atendimento 	= coalesce(nr_atendimento, nr_atendimento_cirurgia_p),
			nr_cirurgia	= nr_cirurgia_p
		where 	nr_sequencia	= nr_sequencia_p;

		select	nr_seq_reserva
		into STRICT	nr_seq_reserva_W
		from	san_transfusao
		where	nr_sequencia =	nr_sequencia_p;

		if (nr_seq_reserva_w IS NOT NULL AND nr_seq_reserva_w::text <> '') then
			update	san_reserva
			set	nr_cirurgia 	= nr_cirurgia_p
			where	nr_sequencia 	= nr_seq_reserva_w
