CREATE OR REPLACE PROCEDURE save_polygraphy_justification ( nr_polygraphy_record_id integra_poligrafo_detalhe.nr_sequencia%TYPE, nm_usuario_nrec_p poligrafo_sit_just.nm_usuario%TYPE, ds_justification_p poligrafo_sit_just.ds_justificativa%TYPE ) AS $body$
BEGIN
    INSERT INTO poligrafo_sit_just(
        nr_sequencia,
        nr_seq_poligrafo,
        dt_atualizacao,
        nm_usuario,
        dt_atualizacao_nrec,
        nm_usuario_nrec,
        ds_justificativa
    ) VALUES (
        nextval('poligrafo_sit_just_seq'),
        nr_polygraphy_record_id,
        clock_timestamp(),
        nm_usuario_nrec_p,
        clock_timestamp(),
        nm_usuario_nrec_p,
        ds_justification_p
    );

    UPDATE integra_poligrafo_detalhe
    SET
        ie_situacao = 'I',
        nm_usuario = nm_usuario_nrec_p,
        dt_atualizacao = clock_timestamp()
    WHERE
        nr_sequencia = nr_polygraphy_record_id;

    COMMIT;
END;
$body$
