CREATE OR REPLACE PROCEDURE scc_alterar_status (nr_seq_interna_p bigint, nr_prescricao_p bigint, ds_sigla_integracao_p text) AS $body$
DECLARE

  ie_status_alterar_w numeric(20);

BEGIN

if (coalesce(nr_seq_interna_p::text, '') = '') then
  CALL WHEB_MENSAGEM_PCK.exibir_mensagem_abort(277600);
end if;

begin
  select r.ie_status_regra
  into STRICT   ie_status_alterar_w
  from   regra_exame_status r
  where  r.ds_sigla_integracao = ds_sigla_integracao_p;
exception
  when too_many_rows then
       CALL WHEB_MENSAGEM_PCK.exibir_mensagem_abort(1091669);

  when no_data_found then
       CALL WHEB_MENSAGEM_PCK.exibir_mensagem_abort(1091670);
end;
    
if (ie_status_alterar_w IS NOT NULL AND ie_status_alterar_w::text <> '') then    
  update prescr_procedimento pp
  set    pp.ie_status_atend = ie_status_alterar_w,
         pp.dt_atualizacao = clock_timestamp(),
         pp.dt_integracao = clock_timestamp(),
         pp.nm_usuario = 'SCC'
  where  pp.nr_seq_interno = nr_seq_interna_p;

  commit;
end if;
    
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
