CREATE OR REPLACE PROCEDURE scc_suspender_exame_prescricao ( nr_prescricao_p bigint, nr_seq_interna_p bigint, ds_motivo_p text, nm_usuario_p text) AS $body$
DECLARE


ie_nr_sequencia   bigint;
nr_prescricao_w   prescr_medica.nr_prescricao%type;


BEGIN

/*if (nr_prescricao_p is null) then
  WHEB_MENSAGEM_PCK.exibir_mensagem_abort(277599);
els*/
if (coalesce(nr_seq_interna_p::text, '') = '') then
  CALL WHEB_MENSAGEM_PCK.exibir_mensagem_abort(277600);
end if;

select max(nr_sequencia),
      max(nr_prescricao)
into STRICT  ie_nr_sequencia,
      nr_prescricao_w
from prescr_procedimento a
where	1=1 --a.nr_prescricao = nr_prescricao_p
and	a.nr_seq_interno = nr_seq_interna_p;

if (ie_nr_sequencia > 0) then
  begin
  CALL Suspender_item_Prescricao(nr_prescricao_w,
        ie_nr_sequencia,
        null,
        ds_motivo_p,
        'PRESCR_PROCEDIMENTO',
        'SCC');
  exception
  when others then
    CALL WHEB_MENSAGEM_PCK.exibir_mensagem_abort(278088,'DS_ERRO='||SQLERRM);
  end;
else
  CALL WHEB_MENSAGEM_PCK.exibir_mensagem_abort(278764);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
