CREATE OR REPLACE PROCEDURE schem4test_generate_test_value ( nr_sequencia_p bigint, nr_seq_action_p bigint, nm_usuario_p text) AS $body$
DECLARE


-- Procedure called in trigger object, shouldn't have commit
C01 CURSOR FOR
	SELECT		v.nr_sequencia nr_sequencia
	from 		schem_test_variable v 
	where 		v.nr_seq_action = nr_seq_action_p
	group by 	v.nr_sequencia, 
				v.nm_variable
	order by 	v.nm_variable;

c01_w C01%rowtype;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin	
	delete 
	from 	schem_test_values
	where 	nr_seq_step = nr_sequencia_p;

    open C01;
    loop
	fetch C01 into
		c01_w;
    EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
        insert into schem_test_values(nr_sequencia, nr_seq_step, nr_seq_variable, ds_value, dt_atualizacao, nm_usuario, nm_usuario_nrec)
        values (nextval('schem_test_values_seq'), nr_sequencia_p, c01_w.nr_sequencia, '', clock_timestamp(), nm_usuario_p, nm_usuario_p);
    end;
	end loop;
	close C01;
	
	exception
	when no_data_found then	
		RAISE NOTICE 'Erro: Data not found';
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
