CREATE OR REPLACE PROCEDURE schematic4test_altersev (NR_SEQUENCIA_P bigint, NR_SEQ_PACK_SCHED_P bigint, IE_TIPO_P text, NR_OS_P text, SEVERIDADE_P text, NM_USUARIO_P text) AS $body$
DECLARE

NR_SEQUENCIA_W bigint;
NR_SEQ_SCRIPT_W bigint;
NR_SEQ_SCHEDULE_W bigint;

C1 CURSOR FOR
SELECT NR_SEQUENCIA
    INTO STRICT NR_SEQUENCIA_W
FROM SCHEM_TEST_SCHEDULE 
WHERE NR_SEQ_PACKAGE = NR_SEQ_PACK_SCHED_P
AND IE_JOBS NOT IN ('1','2')
AND IE_STATUS IN ('3','4','5');


BEGIN
    --procedure that enable or disable SO and alter severity
	INSERT INTO SCHEM_TEST_BEHOLDER(nr_sequencia, nm_usuario, dt_atualizacao, nm_usuario_nrec, dt_atualizacao_nrec, ds_interaction, ds_param_a, ds_param_b, ds_param_c, ds_param_d, ds_param_e)
	VALUES (nextval('schem_test_beholder_seq'), NM_USUARIO_P, clock_timestamp(), 'Robot', clock_timestamp(), 'SCHEMATIC4TEST_ALTERSEV',
	'NR_SEQUENCIA_P as '||NR_SEQUENCIA_P, 
	'NR_SEQ_PACK_SCHED_P as '||NR_SEQ_PACK_SCHED_P, 
	'IE_TIPO_P as '||IE_TIPO_P, 
	'NR_OS_P as '||NR_OS_P, 
	'SEVERIDADE_P as '||SEVERIDADE_P);

    --Enable
    IF (IE_TIPO_P = '1') THEN
      UPDATE SCHEM_TEST_OS SET DT_ATUALIZACAO = clock_timestamp(), NM_USUARIO = nm_usuario_p, IE_SITUACAO = 'A' WHERE NR_SEQUENCIA = NR_SEQUENCIA_P;
    END IF;

    --Disable
    IF (IE_TIPO_P = '2') THEN
        UPDATE SCHEM_TEST_OS SET DT_ATUALIZACAO = clock_timestamp(), NM_USUARIO = nm_usuario_p, IE_SITUACAO = 'I' WHERE NR_SEQUENCIA = NR_SEQUENCIA_P;

        SELECT DISTINCT NR_SEQ_SCRIPT, NR_SEQ_SCHEDULE 
          INTO STRICT NR_SEQ_SCRIPT_W, NR_SEQ_SCHEDULE_W 
        FROM SCHEM_TEST_OS WHERE NR_SEQUENCIA = NR_SEQUENCIA_P;

        IF (NR_SEQ_SCRIPT_W IS NOT NULL AND NR_SEQ_SCRIPT_W::text <> '') THEN
          UPDATE SCHEM_TEST_LOG SET IE_OS  = NULL WHERE NR_SEQ_SCHEDULE = NR_SEQ_SCHEDULE_W AND NR_SEQ_SCRIPT = NR_SEQ_SCRIPT_W;
        END IF;
    END IF;
	
    --Alter severity by SO
    IF (IE_TIPO_P = '3') THEN
      UPDATE SCHEM_TEST_OS SET DT_ATUALIZACAO = clock_timestamp(), NM_USUARIO = NM_USUARIO_P, DS_SEVERIDADE  = SEVERIDADE_P WHERE NR_SEQUENCIA = NR_SEQUENCIA_P AND NR_OS LIKE NR_OS_P;
    END IF;

	commit;

    IF (NR_SEQ_PACK_SCHED_P IS NOT NULL AND NR_SEQ_PACK_SCHED_P::text <> '') THEN
        OPEN C1;
        LOOP
        FETCH C1 INTO
          NR_SEQUENCIA_W;
        EXIT WHEN NOT FOUND; /* apply on C1 */
        BEGIN	
          CALL SCHEMATIC4TEST_SETSCHEDSTATUS(NR_SEQUENCIA_W);
        END;
        END LOOP;
        CLOSE C1;
    END IF;
EXCEPTION
WHEN no_data_found THEN
  RAISE NOTICE 'Erro: Data not found';
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE schematic4test_altersev (NR_SEQUENCIA_P bigint, NR_SEQ_PACK_SCHED_P bigint, IE_TIPO_P text, NR_OS_P text, SEVERIDADE_P text, NM_USUARIO_P text) FROM PUBLIC;

