SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE schematic4test_configpackage (NR_SEQUENCIA_P bigint, NM_USUARIO_P text) AS $body$
DECLARE

NR_SEQ_SUITE_W bigint;

--loop suites in package.
C01 CURSOR FOR
    SELECT DISTINCT item.NR_SEQ_SUITE
       INTO STRICT NR_SEQ_SUITE_W
    FROM SCHEM_TEST_PACKAGE pack, SCHEM_TEST_PACKAGE_ITEM item  
    WHERE item.NR_SEQ_PACKAGE = pack.NR_SEQUENCIA
    AND pack.NR_SEQUENCIA = NR_SEQUENCIA_P;


BEGIN
  --procedure that set profile with machine or user robot to suites of package
	INSERT INTO SCHEM_TEST_BEHOLDER(nr_sequencia, nm_usuario, dt_atualizacao, nm_usuario_nrec, dt_atualizacao_nrec, ds_interaction, ds_param_a)
	VALUES (nextval('schem_test_beholder_seq'), NM_USUARIO_P, clock_timestamp(), 'Robot', clock_timestamp(), 'SCHEMATIC4TEST_CONFIGPACKAGE',
	'NR_SEQUENCIA_P as '||NR_SEQUENCIA_P);

  IF (NR_SEQUENCIA_P IS NOT NULL AND NR_SEQUENCIA_P::text <> '') THEN
    UPDATE SCHEM_TEST_PACKAGE SET DT_ATUALIZACAO = clock_timestamp(), NM_USUARIO = NM_USUARIO_P  WHERE NR_SEQUENCIA = NR_SEQUENCIA_P;
    COMMIT;

    OPEN C01;
    LOOP
    FETCH C01 INTO	
        NR_SEQ_SUITE_W;
    EXIT WHEN NOT FOUND; /* apply on C01 */
    BEGIN	
        UPDATE SCHEM_TEST_PACKAGE_ITEM SET DT_ATUALIZACAO = clock_timestamp(), NM_USUARIO = NM_USUARIO_P WHERE NR_SEQ_PACKAGE = NR_SEQUENCIA_P;
        COMMIT;

        CALL SCHEMATIC4TEST_CONFIGTEST(NR_SEQ_SUITE_W, NM_USUARIO_P);
    END;
    END LOOP;
    CLOSE C01;
  END IF;
  EXCEPTION
  WHEN no_data_found THEN
    RAISE NOTICE 'Erro: Data not found';
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
