SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE schematic4test_gropup_fail (NR_SEQUENCIA_P bigint, NM_USUARIO_P text) AS $body$
DECLARE

NR_SEQUENCIAW bigint;

C01 CURSOR FOR  --loop schedule with fail
SELECT NR_SEQUENCIA
  INTO STRICT NR_SEQUENCIAW
FROM SCHEM_TEST_SCHEDULE
WHERE IE_STATUS LIKE '3' 
AND NR_SEQ_PACKAGE = NR_SEQUENCIA_P;


BEGIN
	--procedure that reexec fails of group to suites with fail
	IF (NR_SEQUENCIA_P IS NOT NULL AND NR_SEQUENCIA_P::text <> '') THEN
		OPEN C01;
		LOOP
		FETCH C01 INTO	
			NR_SEQUENCIAW;
		EXIT WHEN NOT FOUND; /* apply on C01 */
		BEGIN	
			CALL SCHEMATIC4TEST_REEXEC_FAIL(NR_SEQUENCIAW, NM_USUARIO_P);
		END;
		END LOOP;
		CLOSE C01;
	END IF;
	EXCEPTION
	WHEN no_data_found THEN
		RAISE NOTICE 'Erro: Data not found';
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
