CREATE OR REPLACE PROCEDURE schematic4test_sendmail (NR_SEQ_PACKAGE_SCHED bigint, NM_USUARIO_P text) AS $body$
DECLARE

NR_SEQ_SCHEDULE_W bigint;


BEGIN
	--procedure that sendmail
	INSERT INTO SCHEM_TEST_BEHOLDER(nr_sequencia, nm_usuario, dt_atualizacao, nm_usuario_nrec, dt_atualizacao_nrec, ds_interaction, ds_param_a)
	VALUES (nextval('schem_test_beholder_seq'), NM_USUARIO_P, clock_timestamp(), 'Robot', clock_timestamp(), 'SCHEMATIC4TEST_SENDMAIL',
	'NR_SEQ_PACKAGE_SCHED as '||NR_SEQ_PACKAGE_SCHED);

    IF (NR_SEQ_PACKAGE_SCHED IS NOT NULL AND NR_SEQ_PACKAGE_SCHED::text <> '') THEN
        UPDATE SCHEM_TEST_SWITCH SET IE_SWITCH = '1', DS_VALUE = NR_SEQ_PACKAGE_SCHED, NM_USUARIO = NM_USUARIO_P, DT_ATUALIZACAO = clock_timestamp() WHERE NR_INDEX = 200;
        COMMIT;

        SELECT MAX(NR_SEQUENCIA) NR_SEQ_SCHEDULE
          INTO STRICT NR_SEQ_SCHEDULE_W
        FROM SCHEM_TEST_SCHEDULE
        WHERE NR_SEQ_PACKAGE = NR_SEQ_PACKAGE_SCHED;

        CALL SCHEMATIC4TEST_SETSCHEDSTATUS(NR_SEQ_SCHEDULE_W);
    END IF;

    EXCEPTION
    WHEN no_data_found THEN
        RAISE NOTICE 'Erro: Data not found';
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
