CREATE OR REPLACE PROCEDURE schematic4test_setactyincomp (STATUS_P bigint) AS $body$
DECLARE

NR_SEQUENCIA_W bigint;
QTD_W bigint;


BEGIN
  --procedure para setar incompleto.  
  SELECT COUNT(NR_SEQUENCIA) QTD
    INTO STRICT QTD_W
  FROM SCHEM_TEST_PACKAGE_SCHED WHERE NM_USUARIO_NREC LIKE 'Dev-NightBuild' AND IE_STATUS = '10';

  IF (QTD_W <> 0) THEN
    SELECT NR_SEQUENCIA 
      INTO STRICT NR_SEQUENCIA_W 
    FROM SCHEM_TEST_PACKAGE_SCHED WHERE NM_USUARIO_NREC LIKE 'Dev-NightBuild' AND IE_STATUS = '10';

    UPDATE SCHEM_TEST_PACKAGE_SCHED SET IE_STATUS = to_char(STATUS_P), IE_ESTAG_EXEC = '4' WHERE IE_STATUS = '10' AND NR_SEQUENCIA = NR_SEQUENCIA_W;
    COMMIT;
  END IF;

  EXCEPTION
  WHEN no_data_found THEN
    RAISE NOTICE 'Erro: Data not found';
END;
$body$
