CREATE OR REPLACE PROCEDURE schematic4test_updateactivity (NR_SEQUENCIA_P bigint, NR_SEQ_PACKAGE_P bigint, NM_USUARIO_P text) AS $body$
BEGIN
  --procedure that update package to schedule  
  INSERT INTO SCHEM_TEST_BEHOLDER(nr_sequencia, nm_usuario, dt_atualizacao, nm_usuario_nrec, dt_atualizacao_nrec, ds_interaction, ds_param_a, ds_param_b, ds_param_c, ds_param_d)
	VALUES (nextval('schem_test_beholder_seq'), NM_USUARIO_P, clock_timestamp(), 'Robot', clock_timestamp(), 'SCHEMATIC4TEST_UPDATEACTIVITY', 
	'NR_SEQUENCIA_P as '||NR_SEQUENCIA_P,
	null,
	null,
	null);	
  IF (NR_SEQUENCIA_P IS NOT NULL AND NR_SEQUENCIA_P::text <> '') THEN
    UPDATE SCHEM_TEST_SCHEDULE SET NR_SEQ_PACKAGE = NR_SEQ_PACKAGE_P WHERE NR_SEQUENCIA = NR_SEQUENCIA_P;	
    COMMIT;
	CALL SCHEMATIC4TEST_SETSCHEDSTATUS(NR_SEQUENCIA_P);
  END IF;
  EXCEPTION
  WHEN no_data_found THEN
    RAISE NOTICE 'Erro: Data not found';
END;
$body$
