SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE schematic4test_updtnulo () AS $body$
DECLARE

QTD_W bigint;
NR_SEQ_AGROUP_W bigint;
DT_UTILMA_ATUALIZACAO_STEP_W timestamp;
DT_UTILMA_ATUALIZACAO_W timestamp;


BEGIN
  --procedure that set date to snippet null
  SELECT COUNT(NR_SEQUENCIA) NR_SEQUENCIA
      INTO STRICT QTD_W
  FROM SCHEM_TEST_SNIPPET
  WHERE coalesce(DT_LAST_UPDATE::text, '') = '';

  IF (QTD_W <> 0) THEN
    SELECT MAX(NR_SEQUENCIA) NR_SEQUENCIA
      INTO STRICT NR_SEQ_AGROUP_W
    FROM SCHEM_TEST_SNIPPET    
    WHERE coalesce(DT_LAST_UPDATE::text, '') = '';

    IF (NR_SEQ_AGROUP_W IS NOT NULL AND NR_SEQ_AGROUP_W::text <> '') THEN
        SELECT DT_ATUALIZACAO_NREC 
            INTO STRICT DT_UTILMA_ATUALIZACAO_STEP_W
        FROM SCHEM_TEST_STEP 
        WHERE NR_SEQ_SNIPPET = NR_SEQ_AGROUP_W

        AND NR_SEQUENCIA = (SELECT MAX(NR_SEQUENCIA) FROM SCHEM_TEST_STEP 
                        WHERE NR_SEQ_SNIPPET = NR_SEQ_AGROUP_W);
						
        UPDATE SCHEM_TEST_SNIPPET SET DT_LAST_UPDATE = DT_UTILMA_ATUALIZACAO_STEP_W WHERE NR_SEQUENCIA = NR_SEQ_AGROUP_W;
        COMMIT;
    ELSE
      SELECT DT_ATUALIZACAO_NREC
        INTO STRICT DT_UTILMA_ATUALIZACAO_W
      FROM SCHEM_TEST_SNIPPET     
      WHERE NR_SEQUENCIA = NR_SEQ_AGROUP_W;

      UPDATE SCHEM_TEST_SNIPPET SET DT_LAST_UPDATE = DT_UTILMA_ATUALIZACAO_W WHERE NR_SEQUENCIA = NR_SEQ_AGROUP_W;
      COMMIT;
    END IF;
  END IF;
  EXCEPTION
  WHEN no_data_found THEN
    RAISE NOTICE 'Erro: Data not found';
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
