SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE schematic_excluir_objetos ( nr_seq_objeto_p bigint, ie_commit_p text, nr_seq_funcao_schematic_p bigint default null, nr_seq_bo_p bigint default null) AS $body$
DECLARE


/*Este Objeto é chamado recursivamente até excluir todos os filhos. Comita apenas quando excluir o pai*/

nr_sequencia_w	bigint;
qt_obj_w	bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	objeto_schematic
where	nr_seq_obj_sup		= nr_seq_objeto_p
and	nr_seq_funcao_schematic = nr_seq_funcao_schematic_p

union

select	nr_sequencia
from	objeto_schematic
where	nr_seq_obj_sup		= nr_seq_objeto_p
and	nr_seq_bo		= nr_seq_bo_p
and	coalesce(nr_seq_funcao_schematic::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	CALL SCHEMATIC_EXCLUIR_OBJETOS(nr_sequencia_w,'N',nr_seq_funcao_schematic_p,nr_seq_bo_p);

	end;
end loop;
close C01;

if (coalesce(nr_seq_bo_p,0) > 0) then
	update	objeto_schematic
	set	NR_SEQ_OBJ_SUP_ATIV 	 = NULL
	where	NR_SEQ_OBJ_SUP_ATIV 	= nr_seq_objeto_p
	and	nr_seq_bo		= nr_seq_bo_p;
else
	update	objeto_schematic
	set	NR_SEQ_OBJ_SUP_ATIV 	 = NULL
	where	NR_SEQ_OBJ_SUP_ATIV 	= nr_seq_objeto_p
	and	nr_seq_funcao_schematic = nr_seq_funcao_schematic_p;
end if;

select 	count(1)
into STRICT	qt_obj_w
from	opcoes_crud
where	nr_seq_objeto_schematic = nr_seq_objeto_p;

if (qt_obj_w > 0) then
	begin

	CALL gravar_log_tasy(88779,'Delete regra_condicao_item - seq_objeto = ' || nr_seq_objeto_p ||' - seq_func_schem = '||nr_seq_funcao_schematic_p ||' - nr_seq_bo = '||nr_seq_bo_p,wheb_usuario_pck.get_nm_usuario);
	CALL gravar_log_exclusao('REGRA_CONDICAO_ITEM', wheb_usuario_pck.get_nm_usuario, 'NR_SEQ_OBJETO_SCHEMATIC='||nr_seq_objeto_p, 'S');

	delete	FROM regra_condicao_item
	WHERE	NR_SEQ_REGRA IN (SELECT nr_sequencia
		FROM	regra_condicao
		WHERE	NR_SEQ_OPCAO_CRUD IN (SELECT nr_sequencia
			FROM	opcoes_crud
			WHERE	nr_seq_objeto_schematic = nr_seq_objeto_p));

	CALL gravar_log_tasy(88779,'Delete regra_condicao - seq_objeto = ' || nr_seq_objeto_p ||' - seq_func_schem = '||nr_seq_funcao_schematic_p||' - nr_seq_bo = '||nr_seq_bo_p,wheb_usuario_pck.get_nm_usuario);
	CALL gravar_log_exclusao('REGRA_CONDICAO', wheb_usuario_pck.get_nm_usuario, 'NR_SEQ_OBJETO_SCHEMATIC='||nr_seq_objeto_p, 'S');
	delete	FROM regra_condicao
	WHERE	NR_SEQ_OPCAO_CRUD IN (SELECT nr_sequencia
		FROM	opcoes_crud
		WHERE	nr_seq_objeto_schematic = nr_seq_objeto_p);

	CALL gravar_log_tasy(88779,'Delete opcoes_crud - seq_objeto = ' || nr_seq_objeto_p ||' - seq_func_schem = '||nr_seq_funcao_schematic_p||' - nr_seq_bo = '||nr_seq_bo_p,wheb_usuario_pck.get_nm_usuario);
	CALL gravar_log_exclusao('OPCOES_CRUD', wheb_usuario_pck.get_nm_usuario, 'NR_SEQ_OBJETO_SCHEMATIC='||nr_seq_objeto_p, 'S');
	delete	FROM opcoes_crud
	WHERE	nr_seq_objeto_schematic = nr_seq_objeto_p;
	end;
end if;

select 	count(1)
into STRICT	qt_obj_w
from	schematic_relatorio
where	nr_seq_objeto_schematic = nr_seq_objeto_p;

if (qt_obj_w > 0) then
	begin

	CALL gravar_log_tasy(88779,'Delete obj_schematic_ativ_param - seq_objeto = ' || nr_seq_objeto_p ||' - seq_func_schem = '||nr_seq_funcao_schematic_p||' - nr_seq_bo = '||nr_seq_bo_p,wheb_usuario_pck.get_nm_usuario);
	CALL gravar_log_exclusao('OBJ_SCHEMATIC_ATIV_PARAM', wheb_usuario_pck.get_nm_usuario, 'NR_SEQ_OBJETO_SCHEMATIC='||nr_seq_objeto_p, 'S');
	delete	FROM obj_schematic_ativ_param
	where	nr_seq_schematic_relatorio in (SELECT 	nr_sequencia
			from	schematic_relatorio
			where	nr_seq_objeto_schematic = nr_seq_objeto_p);

	CALL gravar_log_tasy(88779,'Delete schematic_relatorio - seq_objeto = ' || nr_seq_objeto_p ||' - seq_func_schem = '||nr_seq_funcao_schematic_p||' - nr_seq_bo = '||nr_seq_bo_p,wheb_usuario_pck.get_nm_usuario);
	CALL gravar_log_exclusao('SCHEMATIC_RELATORIO', wheb_usuario_pck.get_nm_usuario, 'NR_SEQ_OBJETO_SCHEMATIC='||nr_seq_objeto_p, 'S');
	delete	FROM schematic_relatorio
	where	nr_seq_objeto_schematic = nr_seq_objeto_p;
	end;
end if;

CALL gravar_log_tasy(88779,'Delete objeto_schematic - seq_objeto = ' || nr_seq_objeto_p ||' - seq_func_schem = '||nr_seq_funcao_schematic_p||' - nr_seq_bo = '||nr_seq_bo_p,wheb_usuario_pck.get_nm_usuario);
CALL gravar_log_exclusao('OBJETO_SCHEMATIC', wheb_usuario_pck.get_nm_usuario, 'NR_SEQUENCIA='||nr_seq_objeto_p, 'S');

if (coalesce(nr_seq_bo_p,0) > 0) then
	delete	from objeto_schematic
	where	nr_sequencia	= nr_seq_objeto_p
	and	nr_seq_bo	= nr_seq_bo_p
	and	coalesce(nr_seq_funcao_schematic::text, '') = '';
else
	delete	from objeto_schematic
	where	nr_sequencia		= nr_seq_objeto_p
	and	nr_seq_funcao_schematic = nr_seq_funcao_schematic_p;

end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
