SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE schematic_gerar_param_ativ ( nr_seq_ativacao_p bigint default 0, nr_seq_objeto_p bigint DEFAULT NULL, nr_seq_schematic_relatorio_p bigint DEFAULT NULL, nr_seq_obj_ativ_p bigint DEFAULT NULL, nr_seq_obj_evt_acao_p bigint DEFAULT NULL, nr_seq_obj_sch_leg_p bigint DEFAULT NULL, nm_usuario_p text DEFAULT NULL) AS $body$
DECLARE


ds_comando_w 		varchar(10000);
ds_comando_p 		varchar(10000);
nm_param_w		varchar(50);
pos_w			bigint;
nr_seq_obj_proc_w	bigint;
NR_SEQ_DIC_OBJ_SQL_w	bigint;
ds_caracter_w		varchar(1);
contador_w		bigint 	:= 1;
caracteres_validos_w	varchar(255)  :='abcdefghijklmnopqrstuvxywzABCDEFGHIJKLMNOPRQSTUVXYWZ_0123456789';
ie_valido_w		boolean;
nr_seq_apres_w		bigint := 1;
qt_aux_w		bigint;
ie_tipo_componente_w	varchar(15);
nr_seq_cham_externa_w	obj_schematic_evento_acao.nr_seq_cham_externa%type;
ie_tipo_objeto_w	varchar(10);


BEGIN

select	max(ie_tipo_componente),
	max(ie_tipo_objeto)
into STRICT	ie_tipo_componente_w,
	ie_tipo_objeto_w
from	objeto_schematic
where	nr_sequencia	= nr_seq_objeto_p;

if (ie_tipo_objeto_w = 'BO') then

	CALL SCHEMATIC_GERAR_PARAM_BO(nr_seq_objeto_p,nm_usuario_p);

elsif (ie_tipo_componente_w = 'WDF') or (ie_tipo_componente_w = 'WF') then

	CALL SCHEMATIC_GERAR_PARAM_WDF(nr_seq_objeto_p,nm_usuario_p);

elsif (coalesce(nr_seq_ativacao_p,0) > 0) or (coalesce(nr_seq_objeto_p,0) > 0) or (coalesce(nr_seq_obj_ativ_p,0) > 0) or (coalesce(nr_seq_obj_sch_leg_p,0) > 0) then

	if (coalesce(nr_seq_ativacao_p,0) > 0) then

		select	max(ds_sql)
		into STRICT	ds_comando_w
		from	tabela_sistema_ativacao
		where	nr_sequencia = nr_seq_ativacao_p;

	elsif (coalesce(nr_seq_obj_ativ_p,0) > 0) then

		select	max(ds_sql)
		into STRICT	ds_comando_w
		from	objeto_schematic_ativ
		where	nr_sequencia = nr_seq_obj_ativ_p;

	elsif (coalesce(nr_seq_objeto_p,0) > 0) then
		select	max(a.ds_sql)
		into STRICT	ds_comando_w
		from	dic_objeto a,
			objeto_schematic b
		where	a.nr_sequencia 	= b.nr_seq_dic_objeto
		and	b.nr_sequencia	= nr_seq_objeto_p;
	elsif (coalesce(nr_seq_obj_sch_leg_p,0) > 0) then
		select	max(a.ds_sql)
		into STRICT	ds_comando_w
		from	dic_objeto a,
			objeto_schematic_legenda b
		where	a.nr_sequencia 	= b.nr_seq_dic_objeto
		and	b.nr_sequencia	= nr_seq_obj_sch_leg_p;
	end if;

	ds_comando_p := ds_comando_w;

	pos_w := position(':' in ds_comando_w);
	while( pos_w > 0 ) and ( contador_w < 300 ) loop /*Variavel de controle para evitar LOOP*/
		ds_comando_w 	:= substr(ds_comando_w,pos_w+1,length(ds_comando_w));
		nm_param_w 	:= '';
		ie_valido_w	:= true;
		pos_w 		:= 1;
		while( ie_valido_w ) and ( pos_w < length(ds_comando_p)) loop  /*Variavel de controle para evitar LOOP*/
			ds_caracter_w := substr(ds_comando_w,pos_w,1);
			if ( position(ds_caracter_w in caracteres_validos_w) > 0 ) then
				nm_param_w := nm_param_w || ds_caracter_w;
			else
				ie_valido_w := false;
			end if;
			pos_w := pos_w + 1;
		end loop;
		nm_param_w := upper(nm_param_w);

		if (coalesce(nr_seq_ativacao_p,0) > 0) then

			select	count(*)
			into STRICT	qt_aux_w
			from	OBJ_SCHEMATIC_ATIV_PARAM
			where	nr_seq_objeto	= nr_seq_objeto_p
			and	nr_seq_ativacao	= nr_seq_ativacao_p
			and	nm_parametro	= nm_param_w;

		elsif (coalesce(nr_seq_obj_ativ_p,0) > 0) then

			select	count(*)
			into STRICT	qt_aux_w
			from	OBJ_SCHEMATIC_ATIV_PARAM
			where	NR_SEQ_OBJ_ATIVACAO	= nr_seq_obj_ativ_p
			and	nm_parametro		= nm_param_w;

		elsif (coalesce(nr_seq_objeto_p,0) > 0) then

			select	count(*)
			into STRICT	qt_aux_w
			from	OBJ_SCHEMATIC_ATIV_PARAM
			where	nr_seq_objeto		= nr_seq_objeto_p
			and	coalesce(nr_seq_ativacao::text, '') = ''
			and	nm_parametro		= nm_param_w;

		elsif (coalesce(nr_seq_obj_sch_leg_p,0) > 0) then

			select	count(*)
			into STRICT	qt_aux_w
			from	OBJ_SCHEMATIC_ATIV_PARAM
			where	nr_seq_obj_sch_leg	= nr_seq_obj_sch_leg_p
			and	coalesce(nr_seq_ativacao::text, '') = ''
			and	nm_parametro		= nm_param_w;
		end if;

		if (qt_aux_w = 0) and (position('_' in nm_param_w) > 0 ) then

			insert into OBJ_SCHEMATIC_ATIV_PARAM(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_ativacao,
				nr_seq_objeto,
				nm_parametro,
				nr_seq_apres,
				nr_seq_obj_ativacao,
				nr_seq_obj_sch_leg)
			values (nextval('obj_schematic_ativ_param_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_ativacao_p,
				nr_seq_objeto_p,
				nm_param_w,
				nr_seq_apres_w,
				nr_seq_obj_ativ_p,
				nr_seq_obj_sch_leg_p);

			nr_seq_apres_w := nr_seq_apres_w + 1;
		end if;
		ds_comando_w := substr(ds_comando_w,pos_w,length(ds_comando_w));
		pos_w := position(':' in ds_comando_w);
		contador_w := contador_w + 1;
	end loop;

elsif (coalesce(nr_seq_schematic_relatorio_p, 0) > 0) then
	insert into OBJ_SCHEMATIC_ATIV_PARAM(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_ativacao,
		nr_seq_objeto,
		nm_parametro,
		nr_seq_apres,
		nr_seq_schematic_relatorio,
		ie_origem_param,
		vl_parametro)
	SELECT	nextval('obj_schematic_ativ_param_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		null,
		null,
		CD_PARAMETRO,
		nr_seq_apresent,
		nr_seq_schematic_relatorio_p,
		CASE WHEN coalesce(VL_PADRAO::text, '') = '' THEN null  ELSE 'D' END ,
		CASE WHEN coalesce(VL_PADRAO::text, '') = '' THEN null  ELSE VL_PADRAO END
	from	RELATORIO_PARAMETRO
	where	nr_seq_relatorio = 	(SELECT	max(b.nr_seq_relatorio)
					 from	schematic_relatorio b
					 where	b.nr_sequencia = nr_seq_schematic_relatorio_p);


elsif (coalesce(nr_seq_obj_evt_acao_p,0) > 0) then

	select	max(nr_seq_obj_proc),
		max(NR_SEQ_DIC_OBJ_SQL)
	into STRICT	nr_seq_obj_proc_w,
		NR_SEQ_DIC_OBJ_SQL_w
	from	obj_schematic_evento_acao
	where	nr_sequencia = nr_seq_obj_evt_acao_p;

	if (nr_seq_obj_proc_w IS NOT NULL AND nr_seq_obj_proc_w::text <> '') then
		insert into OBJ_SCHEMATIC_ATIV_PARAM(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_ativacao,
			nr_seq_objeto,
			nm_parametro,
			nr_seq_apres,
			nr_seq_schematic_relatorio,
			nr_seq_obj_evt_acao)
		SELECT	nextval('obj_schematic_ativ_param_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			null,
			null,
			nm_parametro,
			nr_seq_apresentacao,
			null,
			nr_seq_obj_evt_acao_p
		from	objeto_sistema_param a
		where	nr_seq_objeto = nr_seq_obj_proc_w
		and	not exists (SELECT 1 from obj_schematic_ativ_param x
					where	x.nr_seq_obj_evt_acao = nr_seq_obj_evt_acao_p
					and	x.nm_parametro = a.nm_parametro);
	end if;

	if (NR_SEQ_DIC_OBJ_SQL_w IS NOT NULL AND NR_SEQ_DIC_OBJ_SQL_w::text <> '') then

		insert into OBJ_SCHEMATIC_ATIV_PARAM(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_ativacao,
			nr_seq_objeto,
			nm_parametro,
			nr_seq_apres,
			nr_seq_schematic_relatorio,
			nr_seq_obj_evt_acao)
		SELECT	nextval('obj_schematic_ativ_param_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			null,
			null,
			NM_CAMPO_BASE,
			NR_SEQ_APRES,
			null,
			nr_seq_obj_evt_acao_p
		from	DIC_OBJETO a
		where	nr_seq_obj_sup = NR_SEQ_DIC_OBJ_SQL_w
		and	not exists (SELECT 1 from obj_schematic_ativ_param x
					where	x.nr_seq_obj_evt_acao = nr_seq_obj_evt_acao_p
					and	x.nm_parametro = a.NM_CAMPO_BASE);

	end if;

	select	max(nr_seq_cham_externa)
	into STRICT	nr_seq_cham_externa_w
	from	obj_schematic_evento_acao
	where	nr_sequencia = nr_seq_obj_evt_acao_p;

	if (nr_seq_cham_externa_w IS NOT NULL AND nr_seq_cham_externa_w::text <> '') then
		insert into OBJ_SCHEMATIC_ATIV_PARAM(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_ativacao,
			nr_seq_objeto,
			nm_parametro,
			nr_seq_apres,
			nr_seq_schematic_relatorio,
			nr_seq_obj_evt_acao)
		SELECT	nextval('obj_schematic_ativ_param_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			null,
			null,
			b.nm_parametro,
			b.nr_seq_apres,
			null,
			nr_seq_obj_evt_acao_p
		from	funcao_chamada_externa a,
			funcao_chamada_ext_param b
		where	b.nr_seq_cham_externa = a.nr_sequencia
		and	b.nr_seq_cham_externa = nr_seq_cham_externa_w;
	end if;

	update	OBJ_SCHEMATIC_ATIV_PARAM
	set	IE_ORIGEM_PARAM  = 'V',
		NM_ATRIBUTO_REF  = 'NM_USUARIO'
	where	nr_seq_obj_evt_acao = nr_seq_obj_evt_acao_p
	and	nm_parametro = 'NM_USUARIO_P';

	update	OBJ_SCHEMATIC_ATIV_PARAM
	set	IE_ORIGEM_PARAM  = 'V',
		NM_ATRIBUTO_REF  = 'CD_ESTAB'
	where	nr_seq_obj_evt_acao = nr_seq_obj_evt_acao_p
	and	nm_parametro = 'CD_ESTABELECIMENTO_P';

end if;


commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE schematic_gerar_param_ativ ( nr_seq_ativacao_p bigint default 0, nr_seq_objeto_p bigint DEFAULT NULL, nr_seq_schematic_relatorio_p bigint DEFAULT NULL, nr_seq_obj_ativ_p bigint DEFAULT NULL, nr_seq_obj_evt_acao_p bigint DEFAULT NULL, nr_seq_obj_sch_leg_p bigint DEFAULT NULL, nm_usuario_p text DEFAULT NULL) FROM PUBLIC;

