SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE schematic_gerar_param_wdf (nr_seq_objeto_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_comando_w 		varchar(10000);
ds_comando_p 		varchar(10000);
nm_param_w		varchar(50);
pos_w			bigint;
ds_caracter_w		varchar(1);
contador_w		bigint 	:= 1;
caracteres_validos_w	varchar(255)  :='abcdefghijklmnopqrstuvxywzABCDEFGHIJKLMNOPRQSTUVXYWZ_0123456789';
ie_valido_w		boolean;
nr_seq_apres_w		bigint := 1;
qt_aux_w		bigint;

c01 CURSOR FOR
SELECT	a.DS_SQL
from	dic_objeto a,
	dic_objeto_filtro b,
	objeto_schematic c
where	a.nr_sequencia 	= b.nr_seq_dic_objeto
and	b.nr_seq_objeto = c.nr_seq_dic_objeto
and	c.nr_sequencia	= nr_seq_objeto_p

union

select	a.DS_SQL
from	dic_objeto a,
	dic_objeto_filtro b,
	objeto_schematic c
where	a.nr_sequencia 	= b.VL_DEFAULT_DIC_OBJ
and	b.nr_seq_objeto = c.nr_seq_dic_objeto
and	c.nr_sequencia	= nr_seq_objeto_p;


BEGIN

open C01;
loop
fetch C01 into
	ds_comando_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_comando_p := ds_comando_w;

	pos_w := position(':'  ds_comando_w);
	while( pos_w > 0 ) and ( contador_w < 300 ) loop /*Variavel de controle para evitar LOOP*/
		ds_comando_w 	:= substr(ds_comando_w,pos_w+1,length(ds_comando_w));
		nm_param_w 	:= '';
		ie_valido_w	:= true;
		pos_w 		:= 1;
		while( ie_valido_w ) and ( pos_w < length(ds_comando_p)) loop  /*Variavel de controle para evitar LOOP*/
			ds_caracter_w := substr(ds_comando_w,pos_w,1);
			if ( position(ds_caracter_w in caracteres_validos_w) > 0 ) then
				nm_param_w := nm_param_w || ds_caracter_w;
			else
				ie_valido_w := false;
			end if;
			pos_w := pos_w + 1;
		end loop;
		nm_param_w := upper(nm_param_w);


		select	count(*)
		into STRICT	qt_aux_w
		from	OBJ_SCHEMATIC_ATIV_PARAM
		where	nr_seq_objeto		= nr_seq_objeto_p
		and	coalesce(nr_seq_ativacao::text, '') = ''
		and	nm_parametro		= nm_param_w;

		if (qt_aux_w = 0) and (position('_' in nm_param_w) > 0 ) then

			insert into OBJ_SCHEMATIC_ATIV_PARAM(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_ativacao,
				nr_seq_objeto,
				nm_parametro,
				nr_seq_apres,
				nr_seq_obj_ativacao)
			values (nextval('obj_schematic_ativ_param_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				null,
				nr_seq_objeto_p,
				nm_param_w,
				nr_seq_apres_w,
				null);

			nr_seq_apres_w := nr_seq_apres_w + 1;
		end if;
		ds_comando_w := substr(ds_comando_w,pos_w,length(ds_comando_w));
		pos_w := position(':' in ds_comando_w);
		contador_w := contador_w + 1;
	end loop;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
